import React from 'react'
import { Autoplay } from 'swiper'
import { Swiper, SwiperSlide } from 'swiper/react'
function Banner() {
  return (
    <div className='relative w-full'>
      <Swiper direction='vertical' spaceBetween={0} slidesPerView={1} loop={true} speed={1000} modules={[Autoplay]} autoplay={{ delay: 4000, disableOnInteraction: false }}>

      </Swiper>
    </div>
  )
}

export default Banner
